sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	var ga = new GlideAjax('GetEmailAjax');
	ga.addParam('sysparm_name', 'getEmail');
	ga.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
	ga.getXML(populateEmail);
   //Type appropriate comment here, and begin script below

	function populateEmail(response){
		var emailFromScriptInclude = response.responseXML.documentElement.getAttribute('answer');
		alert(emailFromScriptInclude);
		g_form.clearValue('u_email_address');
		g_form.setValue('u_email_address' , emailFromScriptInclude);
	}

}
